const skills = tabs.flatMap((tab) => tab.skills);
export const getSkillIcon = (name: string): string => {
	name = name.toLowerCase();

	const skill = skills.find((skill) => skill.name.toLowerCase() === name);
	if (skill) {
		return skill.icon;
	}
	return '';
};

